--//=========================== --//  Dangerous Night Dev Panel --//  All-in-One Script (Rayfield) --//===========================  --== Load Rayfield == local Rayfield = loadstring(game:HttpGet("https://sirius.menu/rayfield"))()  local Window = Rayfield:CreateWindow({     Name = "Dangerous Night Developer Panel",     LoadingTitle = "Initializingâ€¦",     LoadingSubtitle = "Developer Tools",     ConfigurationSaving = {         Enabled = false     } })  --//=========================== --// VARIABLES --//=========================== local player = game.Players.LocalPlayer local char = player.Character or player.CharacterAdded:Wait() local hrp = char:WaitForChild("HumanoidRootPart") local hum = char:WaitForChild("Humanoid")  local ItemsFolder = workspace:WaitForChild("Items") -- ubah jika folder beda local FoodsFolder = workspace:WaitForChild("Foods") -- folder food  local FlyEnabled = false local AutoFood = false local SelectedItem = nil  --//=========================== --// UPDATE ITEM LIST --//=========================== local function getItemList()     local list = {}      for _, item in ipairs(ItemsFolder:GetChildren()) do         table.insert(list, item.Name)     end      return list end  --//=========================== --// PANEL UTAMA --//=========================== local MainTab = Window:CreateTab("Main")  local ItemDropdown = MainTab:CreateDropdown({     Name = "Pilih Item",     Options = getItemList(),     CurrentOption = "",     Callback = function(option)         SelectedItem = option     end })  MainTab:CreateButton({     Name = "Refresh Daftar Item",     Callback = function()         ItemDropdown:Refresh(getItemList(), true)     end })  --//=========================== --// PICKUP ITEM --//=========================== MainTab:CreateButton({     Name = "Pickup Item Terpilih",     Callback = function()         if not SelectedItem then return end          local target = ItemsFolder:FindFirstChild(SelectedItem)         if target then             hrp.CFrame = target.CFrame * CFrame.new(0, 3, 0)             task.wait(.4)             fireproximityprompt(target:FindFirstChildWhichIsA("ProximityPrompt"))         end     end })  --//=========================== --// AUTO FOOD COLLECT --//=========================== MainTab:CreateToggle({     Name = "Auto Collect Food",     CurrentValue = false,     Callback = function(v)         AutoFood = v     end })  task.spawn(function()     while true do         task.wait(.2)         if AutoFood then             for _, f in ipairs(FoodsFolder:GetChildren()) do                 if f:FindFirstChildWhichIsA("ProximityPrompt") then                     hrp.CFrame = f.CFrame * CFrame.new(0, 3, 0)                     task.wait(.15)                     fireproximityprompt(f:FindFirstChildWhichIsA("ProximityPrompt"))                 end             end         end     end end)  --//=========================== --// SPEED CONTROL --//=========================== MainTab:CreateSlider({     Name = "Player Speed",     Range = {16, 120},     Increment = 1,     CurrentValue = 16,     Callback = function(val)         hum.WalkSpeed = val     end })  --//=========================== --// FLY MODE --//=========================== MainTab:CreateToggle({     Name = "Fly Mode",     CurrentValue = false,     Callback = function(v)         FlyEnabled = v     end })  -- basic fly loop task.spawn(function()     while true do         task.wait()         if FlyEnabled then             hum.PlatformStand = true             local move = Vector3.zero              if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.W) then                 move = move + hrp.CFrame.LookVector             end             if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.S) then                 move = move - hrp.CFrame.LookVector             end             if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.A) then                 move = move - hrp.CFrame.RightVector             end             if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.D) then                 move = move + hrp.CFrame.RightVector             end             if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.Space) then                 move = move + Vector3.new(0, 1, 0)             end             if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.LeftShift) then                 move = move - Vector3.new(0, 1, 0)             end              hrp.Velocity = move * 50         else             hum.PlatformStand = false         end     end end)  --//=========================== --// DROP ITEM --//=========================== MainTab:CreateButton({     Name = "Drop Item di Inventory",     Callback = function()         local inv = player:WaitForChild("Backpack")          for _, tool in ipairs(inv:GetChildren()) do             tool.Parent = char             task.wait(.1)             tool:Deactivate()             hum:UnequipTools()         end     end })  Rayfield:Notify({     Title = "Loaded",     Content = "Developer panel siap!",     Duration = 4 })
